def vignereAlphabetSetup(keyWord_lets, alphabet):
    alphabet = alphabet.copy()
    for y in range(len(keyWord_lets)):
        try:
            alphabet.remove(keyWord_lets[0])
        except:
            print(keyWord_lets[0], "is not a letter")
        alphabet.insert(y, keyWord_lets.pop(0))
    return(alphabet)

def Key_Diff_Table(Keyword_2, diff_Table):
    Key_List = list(Keyword_2.upper())
    key_diff_Table = []
    for key in Key_List:
        key_diff_Table.append(diff_Table[key])
    return key_diff_Table

def Encode_Message_func(diff_Table, key_diff_Table, Message, Alphabet):
    key_Table_Length = len(key_diff_Table)
    Encoded_Message = ""
    Message_List = list(Message.upper())
    for i in range(len(Message_List)):
        key_code = i%key_Table_Length
        key_diff = key_diff_Table[key_code]
        letter = Message_List[i]
        letter_Index = diff_Table[letter]

        diff = (key_diff + letter_Index)%len(Alphabet)
        Encoded_Message = Encoded_Message + Alphabet[diff]
    return Encoded_Message

def Encode_Message(Keyword_1, Keyword_2, Message):
    Keyword_Letters = list(Keyword_1.upper())
    alphabet = vignereAlphabetSetup(Keyword_Letters, Alphabet)
    diff_Table = Diff_Table(alphabet)
    key_diff_Table = Key_Diff_Table(Keyword_2, diff_Table)
    encoded_Message = Encode_Message_func(diff_Table, key_diff_Table, Message, alphabet)
    return(encoded_Message)

def Decode_Message_func(diff_Table, key_diff_Table, Encoded_message, Alphabet):
    key_Table_Length = len(key_diff_Table)
    Decoded_Messages = ""
    with alive_bar(len(Encoded_message)) as bar:
        for i in range(len(Encoded_message)):
            key_code = i%key_Table_Length
            key_diff = key_diff_Table[key_code]
            letter = Encoded_message[i]
            letter_Index = diff_Table[letter]

            diff = (letter_Index - key_diff)%len(Alphabet)
            Decoded_Messages = Decoded_Messages + Alphabet[diff]
            time.sleep(0.4)
            bar()
    return Decoded_Messages

def Decode_Message(Keyword_1, Keyword_2, Message):
    Message = list(Message.upper())
    Keyword_letter = list(Keyword_1.upper())
    alphabet = vignereAlphabetSetup(Keyword_letter, Alphabet)
    diff_Table = Diff_Table(alphabet)
    key_Diff_Table = Key_Diff_Table(Keyword_2, diff_Table)
    decoded_Message = Decode_Message_func(diff_Table, key_Diff_Table, Message, alphabet)
    return (decoded_Message)

